mapper.select:
select dh.Number,concat(dh.SubType,dh.Type) as newType,s.supplier,dh.ChangeAmount,date_format(dh.OperTime,'%Y-%m-%d %H:%i:%S') as oTime,'' as AList,'' as AMList from depothead dh inner join supplier s on dh.OrganId = s.id where 1=1
and dh.AccountId=${accountId}
UNION ALL select ah.BillNo,ah.Type as newType,s.supplier,ah.ChangeAmount,date_format(ah.BillTime,'%Y-%m-%d %H:%i:%S') as oTime,'' as AList,'' as AMList from accounthead ah inner join supplier s on ah.OrganId=s.id where 1=1
and ah.AccountId=${accountId}
UNION ALL select ah.BillNo,ah.Type as newType,s.supplier,ai.EachAmount,date_format(ah.BillTime,'%Y-%m-%d %H:%i:%S') as oTime,'' as AList,'' as AMList from accounthead ah inner join supplier s on ah.OrganId=s.id inner join accountitem ai on ai.HeaderId=ah.Id where ah.Type in ('收款','付款','收预付款')
and ai.AccountId=${accountId}
UNION ALL select ah.BillNo,ah.Type as newType, '' as sName,ah.ChangeAmount,date_format(ah.BillTime,'%Y-%m-%d %H:%i:%S') as oTime,'' as AList,'' as AMList from accounthead ah inner join accountitem ai on ai.HeaderId=ah.Id where ah.Type='转账'
and ah.AccountId=${accountId}
UNION ALL select ah.BillNo,ah.Type as newType, '' as sName,ai.EachAmount,date_format(ah.BillTime,'%Y-%m-%d %H:%i:%S') as oTime,'' as AList,'' as AMList from accounthead ah inner join accountitem ai on ai.HeaderId=ah.Id where ah.Type='转账'
and ai.AccountId=${accountId}
UNION ALL select dh.Number,concat(dh.SubType,dh.Type) as newType,s.supplier,dh.ChangeAmount,date_format(dh.OperTime,'%Y-%m-%d %H:%i:%S') as oTime, dh.AccountIdList as AList,dh.AccountMoneyList as AMList from depothead dh inner join supplier s on dh.OrganId = s.id where 1=1
and dh.AccountIdList like '%${accountId}%'
ORDER BY oTime desc
limit #{offset},#{rows}

select sum(a) from (
select count(1) a from depothead dh inner join supplier s on dh.OrganId = s.id where 1=1
and dh.AccountId=${accountId}
UNION ALL select count(1) a from accounthead ah inner join supplier s on ah.OrganId=s.id where 1=1
and ah.AccountId=${accountId}
UNION ALL select count(1) a from accounthead ah inner join supplier s on ah.OrganId=s.id inner join accountitem ai on ai.HeaderId=ah.Id where ah.Type in ('收款','付款','收预付款')
and ai.AccountId=${accountId}
UNION ALL select count(1) a from accounthead ah inner join accountitem ai on ai.HeaderId=ah.Id where ah.Type='转账'
and ah.AccountId=${accountId}
UNION ALL select count(1) a from accounthead ah inner join accountitem ai on ai.HeaderId=ah.Id where ah.Type='转账'
and ai.AccountId=${accountId}
UNION ALL select count(1) a from depothead dh inner join supplier s on dh.OrganId = s.id where 1=1
and dh.AccountIdList like '%${accountId}%'
) cc
