mapper.select:
select ser.id, ser.material_Id, ser.serial_Number, ser.is_Sell, ser.remark, ser.delete_Flag, ser.create_Time, ser.update_Time,mat.name as materialName,null as creator,null as updater,null as creatorName, null as updaterName,ser.depothead_Id, dh.number as depotHeadNumber,concat(dh.SubType,dh.Type) as depotHeadType FROM serial_number ser left join material mat on mat.id = ser.material_Id left join depothead dh on dh.id= ser.depothead_Id where 1=1
and ser.serial_Number like #{serialNumber}
and mat.name like #{materialName}
order by ser.id desc
limit #{offset},#{rows}

SELECT COUNT(ser.id) FROM serial_number ser left JOIN material mat on mat.id = ser.material_Id where 1=1
and ser.serial_Number like #{serialNumber}
and mat.name like #{materialName}
order by ser.id desc

SELECT ser.id, ser.material_Id, ser.serial_Number, ser.is_Sell, ser.remark, ser.delete_Flag, ser.create_Time, ser.update_Time,ser.creator,ser.updater,ser.depothead_Id FROM serial_number ser where 1=1
and ser.serial_Number=#{serialNumber}
order by ser.id desc

SELECT count(ser.id) FROM serial_number ser where 1=1
and ser.material_Id=#{materialId}
and ser.delete_Flag !='1' and ser.is_Sell !='1'

SELECT
count(ser.id)
FROM serial_number ser
where 1=1

    and ser.material_Id=#{materialId}


    and ser.depothead_Id=#{depotheadId}

and ser.is_Sell =#{isSell,jdbcType=VARCHAR}
and ser.delete_Flag !='1'

mapper.update:
update serial_number

        is_Sell = '0',

        update_Time = #{updateTime},


        updater = #{updater},


where 1=1

    and material_Id = #{materialId}


    and depothead_Id = #{depotheadId,jdbcType=BIGINT}

    and is_Sell != '0'

    and id in
    ( select batchSN.id from
    ( select selFrom.id from serial_number selFrom
    where 1=1

        and selFrom.material_Id = #{materialId}


        and selFrom.depothead_Id = #{depotheadId,jdbcType=BIGINT}

        and selFrom.is_Sell !='0'
    limit 0,#{count}
    ) batchSN
    )
